SELECT `source`.`blueMiddle` AS `blueMiddle`, `source`.`blueMiddleChamp` AS `blueMiddleChamp`, count(*) AS `count`
FROM (SELECT `tabela_matchinfo`.`League` AS `League`, `tabela_matchinfo`.`Year` AS `Year`, `tabela_matchinfo`.`Season` AS `Season`, `tabela_matchinfo`.`Type` AS `Type`, `tabela_matchinfo`.`blueTeamTag` AS `blueTeamTag`, `tabela_matchinfo`.`blueMiddle` AS `blueMiddle`, `tabela_matchinfo`.`blueMiddleChamp` AS `blueMiddleChamp`, count(*) AS `count` FROM `tabela_matchinfo`
WHERE `tabela_matchinfo`.`bResult` = TRUE
GROUP BY `tabela_matchinfo`.`League`, `tabela_matchinfo`.`Year`, `tabela_matchinfo`.`Season`, `tabela_matchinfo`.`Type`, `tabela_matchinfo`.`blueTeamTag`, `tabela_matchinfo`.`blueMiddle`, `tabela_matchinfo`.`blueMiddleChamp`
ORDER BY `tabela_matchinfo`.`League` ASC, `tabela_matchinfo`.`Year` ASC, `tabela_matchinfo`.`Season` ASC, `tabela_matchinfo`.`Type` ASC, `tabela_matchinfo`.`blueTeamTag` ASC, `tabela_matchinfo`.`blueMiddle` ASC, `tabela_matchinfo`.`blueMiddleChamp` ASC) `source` WHERE (lower(`source`.`blueMiddle`) like '%bjergsen%') GROUP BY `source`.`blueMiddle`, `source`.`blueMiddleChamp` ORDER BY `count` DESC, `source`.`blueMiddle` ASC, `source`.`blueMiddleChamp` ASC
